sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var UserImpersonate = x_snc_devtools.UserImpersonate;
	var RenderValue = x_snc_devtools.RenderValue;
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.incident.active','true'); 
	AppSetProperty('tables.incident.columns.whitelist_trigger.sys_user_group','assignment_group'); 
	AppSetProperty('tables.incident.columns.instant_access.sys_user','opened_by,caller_id'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserCaller = testdata.CreateUser('CALLER',['itil']);
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',['itil']);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',['itil']);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',['itil']);
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);
	
	var strIncidentMember = testdata.CreateIncident('ALPHA_MEMBER','ALPHA_MEMBER','CALLER','ALPHA_MEMBER');
	var strIncidentFulfiller = testdata.CreateIncident('ALPHA_FULFILLER','ALPHA_FULFILLER','CALLER','ALPHA_FULFILLER');

	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentMember,true);

	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentFulfiller,true);
	
	
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
