sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.sc_request.active','true'); 
	AppSetProperty('tables.sc_request.columns.whitelist_trigger.sys_user_group','assignment_group'); 
	AppSetProperty('tables.sc_request.columns.instant_access.sys_user','opened_by,requested_for'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserRequester = testdata.CreateUser('REQUESTER',['itil']);
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',['itil']);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',['itil']);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',['itil']);
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);
	
	var strRequestMember = testdata.CreateRequest('ALPHA_MEMBER','ALPHA_MEMBER','REQUESTER','ALPHA_MEMBER');
	var strRequestFulfiller = testdata.CreateRequest('ALPHA_FULFILLER','ALPHA_FULFILLER','REQUESTER','ALPHA_FULFILLER');

	
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,      'sc_request','',strRequestMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,      'sc_request','',strRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,    'sc_request','',strRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller, 'sc_request','',strRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'sc_request','',strRequestMember,true);

	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,      'sc_request','',strRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,      'sc_request','',strRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,    'sc_request','',strRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller, 'sc_request','',strRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'sc_request','',strRequestFulfiller,true);
	
	
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
