sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsObject = x_snc_devtools.IsObject;
	var GetValue = x_snc_devtools.GetValue;
	var GetUserRecord = x_snc_devtools.GetUserRecord;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.incident.active','true'); 
	AppSetProperty('tables.incident.columns.instant_access.sys_user','opened_by,caller_id'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserCaller = testdata.CreateUser('CALLER',['itil']);
	
	var strIncident = testdata.CreateIncident('TEST','','CALLER','');

	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncident,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncident,true);


	function TestCase_AccessAnalysisForUserAndRecord(strUser, strTable, strRecord, vExpected)
	{
		function TestCase_AccessAnalysisForUserAndRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (IsValidRecord(GetUserRecord(GetValue(vInput,'User'))) == false)
			{
				if (vResult == false)
				{
					return true;
				}
				return false;
			}
			if (IsObject(vResult) == false)
			{
				return false;
			}
			var bAccess = GetValue(vResult,'access');
			if (vExpected != bAccess)
			{
				return false;
			}
			var strExplanation = GetStringValue(GetValue(vResult,'explanation'));
			if (vExpected == true)
			{
				if (strExplanation.indexOf('can potentially access the record') == -1)
				{
					return false;
				}
			}
			if (vExpected == false)
			{
				if (strExplanation.indexOf('can NOT access the record due to DataSilos filtering') == -1)
				{
					return false;
				}
			}
			return true;
		}
		
		test.TestCase(
			'Function AccessAnalysisForUserAndRecord()',
			AccessAnalysisForUserAndRecord,
			{
				'User': strUser,
				'Table': strTable,
				'Record': strRecord
			},
			vExpected,
			TestCase_AccessAnalysisForUserAndRecord_CheckResult);
	}

	TestCase_AccessAnalysisForUserAndRecord('','incident',strIncident,false);
	TestCase_AccessAnalysisForUserAndRecord(strUserSpectator,'incident',strIncident,false);
	TestCase_AccessAnalysisForUserAndRecord(strUserCaller,'incident',strIncident,true);
	

	
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
