sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var UserImpersonate = x_snc_devtools.UserImpersonate;
	var RenderValue = x_snc_devtools.RenderValue;
	var RecordSetValue = x_snc_devtools.RecordSetValue;
	var GetStringValue = x_snc_devtools.GetStringValue;


	var bResult = true;
	
	function TestCase_RecordQuery(vInputUser, vInputTable, vInputPrefix, vInputSysId, vExpected)
	{
		Log('TestCase_RecordQuery');
		
		var grUser = new GlideRecord('sys_user');
		grUser.get(vInputUser);
		
		Log('vInputUser : '+RenderValue(vInputUser) + ' '+GetStringValue(grUser.name));
		Log('vInputTable: '+RenderValue(vInputTable));
		Log('vInputPrefix: '+RenderValue(vInputPrefix));
		Log('vInputSysId: '+RenderValue(vInputSysId));
		Log('vExpected: '+RenderValue(vExpected));
		
		var bRecordFound = false;
		
		// 1. Impersonate
		Log('Impersonate user: '+vInputUser);
		var strCurrentUser = UserImpersonate(vInputUser);
		
		// 2. Query
		Log('record get: '+vInputTable+' - '+vInputSysId);
		var grRecord = new GlideRecordSecure(vInputTable);
		grRecord.addQuery(vInputPrefix+'sys_id','=',vInputSysId);
		grRecord.query();
		if (grRecord.next())
		{
			Log('get returned true!');
			if (grRecord.canRead() == true)
			{
				Log('can read returned true!');
				bRecordFound = true;
			}
		}
		
		// 3. Unimpersonate
		UserImpersonate(strCurrentUser);
				
		
		Log('Result  : '+RenderValue(bRecordFound));
		if (bRecordFound != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	
	// 1. Create test data
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var aUserRoles = ['sn_incident_read'];
	var aSupportRoles = ['itil'];
	var aSentinelRoles = ['itil','x_snc_datasilos.sentinel'];
	
	var strUserSentinel = testdata.CreateUser('SENTINEL',aSentinelRoles);
	var strUserSpectator = testdata.CreateUser('SPECTATOR',aSupportRoles);

	var strUserCaller = testdata.CreateUser('CALLER',aUserRoles);
	var strUserOpener = testdata.CreateUser('OPENER',aUserRoles);
	
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',aSupportRoles);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',aSupportRoles);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',aSupportRoles);

	var strUserBetaMember = testdata.CreateUser('BETA_MEMBER',aSupportRoles);
	var strUserBetaFulfiller = testdata.CreateUser('BETA_FULFILLER',aSupportRoles);
	var strUserBetaSupervisor = testdata.CreateUser('BETA_SUPERVISOR',aSupportRoles);
	
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);

	var strGroupBetaMember = testdata.CreateGroup('BETA_MEMBER',['BETA_MEMBER']);
	var strGroupBetaFulfiller = testdata.CreateGroup('BETA_FULFILLER',['BETA_FULFILLER']);
	var strGroupBetaSupervisor = testdata.CreateGroup('BETA_SUPERVISOR',['BETA_SUPERVISOR']);
	
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);	
	var strSiloBeta = testdata.CreateSilo('BETA',['BETA_MEMBER'],['BETA_FULFILLER'],['BETA_SUPERVISOR']);

	var strTable = 'incident_sla';
	var strPrefix = 'inc_';
	
	//var strTable = 'u_incident_test';
	//var strPrefix = 'inc_';
	
	//var strTable = 'incident';
	//var strPrefix = '';
	
	Log('\nCreate incident\n');
	var strIncident = testdata.CreateIncident('TEST','OPENER','CALLER','ALPHA_MEMBER');
		
	TestCase_RecordQuery(strUserSentinel,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserSpectator,strTable,strPrefix,strIncident,false);

	TestCase_RecordQuery(strUserCaller,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserOpener,strTable,strPrefix,strIncident,true);

	TestCase_RecordQuery(strUserAlphaMember,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserAlphaFulfiller,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserAlphaSupervisor,strTable,strPrefix,strIncident,true);

	TestCase_RecordQuery(strUserBetaMember,strTable,strPrefix,strIncident,false);
	TestCase_RecordQuery(strUserBetaFulfiller,strTable,strPrefix,strIncident,false);
	TestCase_RecordQuery(strUserBetaSupervisor,strTable,strPrefix,strIncident,false);
	

	Log('\nMove incident to member team in beta silo\n');
	RecordSetValue('incident',strIncident,'assignment_group',strGroupBetaMember);
	
	TestCase_RecordQuery(strUserSentinel,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserSpectator,strTable,strPrefix,strIncident,false);

	TestCase_RecordQuery(strUserCaller,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserOpener,strTable,strPrefix,strIncident,true);

	TestCase_RecordQuery(strUserAlphaMember,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserAlphaFulfiller,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserAlphaSupervisor,strTable,strPrefix,strIncident,true);

	TestCase_RecordQuery(strUserBetaMember,strTable,strPrefix,strIncident,true);
	TestCase_RecordQuery(strUserBetaFulfiller,strTable,strPrefix,strIncident,false);
	TestCase_RecordQuery(strUserBetaSupervisor,strTable,strPrefix,strIncident,true);

	
	
	//Log(RenderValue(testdata.GetAllData(),true));
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
