sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.incident.active','true'); 
	AppSetProperty('tables.incident.columns.whitelist_trigger.sys_user_group','assignment_group'); 
	AppSetProperty('tables.incident.columns.instant_access.sys_user','opened_by,caller_id'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserCaller = testdata.CreateUser('CALLER',['itil']);
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',['itil']);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',['itil']);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',['itil']);
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);
	
	var strUserBetaMember = testdata.CreateUser('BETA_MEMBER',['itil']);
	var strUserBetaFulfiller1 = testdata.CreateUser('BETA_FULFILLER_1',['itil']);
	var strUserBetaFulfiller2 = testdata.CreateUser('BETA_FULFILLER_2',['itil']);
	var strUserBetaSupervisor = testdata.CreateUser('BETA_SUPERVISOR',['itil']);
	var strGroupBetaMember = testdata.CreateGroup('BETA_MEMBER',['BETA_MEMBER']);
	var strGroupBetaFulfiller1 = testdata.CreateGroup('BETA_FULFILLER_1',['BETA_FULFILLER_1']);
	var strGroupBetaFulfiller2 = testdata.CreateGroup('BETA_FULFILLER_2',['BETA_FULFILLER_2']);
	var strGroupBetaSupervisor = testdata.CreateGroup('BETA_SUPERVISOR',['BETA_SUPERVISOR']);
	var strSiloBeta = testdata.CreateSilo('BETA',['BETA_MEMBER'],['BETA_FULFILLER_1','BETA_FULFILLER_2'],['BETA_SUPERVISOR']);	
	
	testdata.CreateSuperSiloRelationShip('ALPHA','BETA');
	
	var strIncidentAlphaMember = testdata.CreateIncident('ALPHA_MEMBER','ALPHA_MEMBER','CALLER','ALPHA_MEMBER');
	var strIncidentAlphaFulfiller = testdata.CreateIncident('ALPHA_FULFILLER','ALPHA_FULFILLER','CALLER','ALPHA_FULFILLER');

	var strIncidentBetaMember = testdata.CreateIncident('BETA_MEMBER','BETA_MEMBER','CALLER','BETA_MEMBER');
	var strIncidentBetaFulfiller = testdata.CreateIncident('BETA_FULFILLER','BETA_FULFILLER_1','CALLER','BETA_FULFILLER_1');
	
	
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentAlphaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentAlphaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentAlphaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentAlphaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentAlphaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaMember,'incident','',strIncidentAlphaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller1,'incident','',strIncidentAlphaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller2,'incident','',strIncidentAlphaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaSupervisor,'incident','',strIncidentAlphaMember,false);
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentAlphaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentAlphaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentAlphaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentAlphaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentAlphaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaMember,'incident','',strIncidentAlphaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller1,'incident','',strIncidentAlphaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller2,'incident','',strIncidentAlphaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaSupervisor,'incident','',strIncidentAlphaFulfiller,false);


	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentBetaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentBetaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentBetaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentBetaMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentBetaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaMember,'incident','',strIncidentBetaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller1,'incident','',strIncidentBetaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller2,'incident','',strIncidentBetaMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaSupervisor,'incident','',strIncidentBetaMember,true);
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncidentBetaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncidentBetaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'incident','',strIncidentBetaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'incident','',strIncidentBetaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'incident','',strIncidentBetaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaMember,'incident','',strIncidentBetaFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller1,'incident','',strIncidentBetaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaFulfiller2,'incident','',strIncidentBetaFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserBetaSupervisor,'incident','',strIncidentBetaFulfiller,true);
	
	
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
