sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	var RecordSetValue = x_snc_devtools.RecordSetValue;
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.change_request.active','true'); 
	AppSetProperty('tables.change_request.columns.whitelist_trigger.sys_user_group','assignment_group'); 
	AppSetProperty('tables.change_request.columns.instant_access.sys_user','opened_by,requested_by'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserRequester = testdata.CreateUser('REQUESTER',['itil']);
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',['itil']);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',['itil']);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',['itil']);
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);
	
	var strChangeRequestMember = testdata.CreateChangeRequest('ALPHA_MEMBER','ALPHA_MEMBER','REQUESTER','ALPHA_MEMBER');
	var strChangeRequestFulfiller = testdata.CreateChangeRequest('ALPHA_FULFILLER','ALPHA_FULFILLER','REQUESTER','ALPHA_FULFILLER');

	// Now move the change requests into the 'change_request_imac' table
	RecordSetValue('change_request',strChangeRequestMember,'sys_class_name','change_request_imac');
	RecordSetValue('change_request',strChangeRequestFulfiller,'sys_class_name','change_request_imac');
	
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);

	
	test.Log('Check if the protection of the change_request table inherently protects records in the derived table "change_request_imac"'); 
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'change_request','',strChangeRequestMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,'change_request','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'change_request','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'change_request','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'change_request','',strChangeRequestMember,true);

	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'change_request','',strChangeRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,'change_request','',strChangeRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'change_request','',strChangeRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'change_request','',strChangeRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'change_request','',strChangeRequestFulfiller,true);

	test.Log('Check if records requested from the derived table are also protected by the filter on the super class');  
	
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'change_request_imac','',strChangeRequestMember,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,'change_request_imac','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'change_request_imac','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'change_request_imac','',strChangeRequestMember,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'change_request_imac','',strChangeRequestMember,true);

	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'change_request_imac','',strChangeRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserRequester,'change_request_imac','',strChangeRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,'change_request_imac','',strChangeRequestFulfiller,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller,'change_request_imac','',strChangeRequestFulfiller,true);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'change_request_imac','',strChangeRequestFulfiller,true);
	
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
