sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ModifyACLScript(strScript, strExpected)
	{
		test.TestCase(
			'Function ModifyACLScript()',
			ModifyACLScript,
			{
				'Script': strScript 
			},
			strExpected,
			null);
	}


	var strExpected = "answer = x_snc_datasilos.ExecuteACL(current);";
	TestCase_ModifyACLScript(null,strExpected);
	TestCase_ModifyACLScript('',strExpected);
	TestCase_ModifyACLScript('    ',strExpected);
	

	var strExpected = ""
		+"var bAnswer = false;\n"
		+"bAnswer = SomeFunction();\n"
		+"if (bAnswer == true)\n"
		+"{\n"
		+"\tbAnswer = x_snc_datasilos.ExecuteACL(current);\n"
		+"}\n"
		+"answer = bAnswer;";		
	TestCase_ModifyACLScript("SomeFunction();",strExpected);
	TestCase_ModifyACLScript("answer = SomeFunction();",strExpected);
	
	
	var strExpected = ""
			+"var bAnswer = false;\n"
			+"function GetAnswer()\n"
			+"{\n"
			+"\tvar answer = false;\n"
			+"var value = SomeFunction();\n"
			+"answer = SomeOtherFunction(value);\n"
			+"\treturn answer;\n"
			+"}\n"
			+"bAnswer = GetAnswer();\n"
			+"if (bAnswer == true)\n"
			+"{\n"
			+"\tbAnswer = x_snc_datasilos.ExecuteACL(current);\n"
			+"}\n"
			+"answer = bAnswer;";	

	var strScript = ""
		+"var value = SomeFunction();\n"
		+"answer = SomeOtherFunction(value);";
	
	TestCase_ModifyACLScript(strScript,strExpected);


	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
