var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	var GetType = x_snc_devtools.GetType;
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var bResult = true;
	
	function TestCase_Maintenance(
		vInputFilter,
		strInputTopic,
		callbackInputFunction,
		nInputWindowSize,
		nInputWindowIndex,
		nInputWindowBorderSize,
		vExpected)
	{
		Log('TestCase_Maintenance');
		var strFilterClassName = '';
		if (vInputFilter != undefined)
		{
			strFilterClassName = vInputFilter.getRecordClassName();
		}
		Log('Class name        : '+strFilterClassName);
		Log('Topic             : '+RenderValue(strInputTopic));
		Log('callback          : '+GetType(callbackInputFunction));
		Log('Window size       : '+RenderValue(nInputWindowSize));
		Log('Window index      : '+RenderValue(nInputWindowIndex));
		Log('Window border size: '+RenderValue(nInputWindowBorderSize));
		
		Log('Expected: '+RenderValue(vExpected));
		var vResult = Maintenance(
			vInputFilter,
			strInputTopic,
			callbackInputFunction,
			nInputWindowSize,
			nInputWindowIndex,
			nInputWindowBorderSize);
		Log('Result  : '+RenderValue(vResult));
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	// Prepare general test data
	testdata.CreateUser('CALLER');
	testdata.CreateUser('ALBERT');
	var strGroupAlphaSysId = testdata.CreateGroup('ALPHA',['ALBERT']);

	
	
	// Test scenario 1

	var strIncidentSysId = testdata.CreateIncident('TEST_1','','CALLER','');
	var strChangeRequestSysId = testdata.CreateChangeRequest('TEST_1','','');
	var strProblemSysId = testdata.CreateProblem('TEST_1','','');

	
	var grIncidentFilter = new GlideRecord('incident');
	grIncidentFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());

	var grChangeRequestFilter = new GlideRecord('change_request');
	grChangeRequestFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());

	var grProblemFilter = new GlideRecord('problem');
	grProblemFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());
	
	
	// Prepare an incident with an empty whitelist
	var grIncident1 = new GlideRecord('incident');
	if (grIncident1.get(strIncidentSysId))
	{
		grIncident1.assignment_group = strGroupAlphaSysId;
		grIncident1.x_snc_datasilos_group_whitelist = '';
		grIncident1.update();
	}
	TestCase_Maintenance(grIncidentFilter,'Test incident',null,0,0,0,1);

	
	// Prepare a change request with an empty whitelist
	var grChangeRequest1 = new GlideRecord('change_request');
	if (grChangeRequest1.get(strChangeRequestSysId))
	{
		grChangeRequest1.assignment_group = strGroupAlphaSysId;
		grChangeRequest1.x_snc_datasilos_group_whitelist = '';
		grChangeRequest1.update();
	}
	TestCase_Maintenance(grChangeRequestFilter,'Test change_request',null,0,0,0,1);

	
	// Prepare a problem with an empty whitelist
	var grProblem1 = new GlideRecord('problem');
	if (grProblem1.get(strProblemSysId))
	{
		grProblem1.assignment_group = strGroupAlphaSysId;
		grProblem1.x_snc_datasilos_group_whitelist = '';
		grProblem1.update();
	}
	TestCase_Maintenance(grProblemFilter,'Test problem',null,0,0,0,1);
	
	

	
	
	// Test scenario 2 - using a callback function
	
	function RecordProcessorTest2(record,nRow)
	{
		Log('Using RecordProcessorTest2 for a record in table ['+record.getRecordClassName()+'] on row '+nRow);
		RecordUpdate(record);
	}

	var strIncidentSysId = testdata.CreateIncident('TEST_2','','CALLER','');
	var strChangeRequestSysId = testdata.CreateChangeRequest('TEST_2','','');
	var strProblemSysId = testdata.CreateProblem('TEST_2','','');

	
	var grIncidentFilter = new GlideRecord('incident');
	grIncidentFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());

	var grChangeRequestFilter = new GlideRecord('change_request');
	grChangeRequestFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());

	var grProblemFilter = new GlideRecord('problem');
	grProblemFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());
	
	
	// Prepare an incident with an empty whitelist
	var grIncident1 = new GlideRecord('incident');
	if (grIncident1.get(strIncidentSysId))
	{
		grIncident1.assignment_group = strGroupAlphaSysId;
		grIncident1.x_snc_datasilos_group_whitelist = '';
		grIncident1.update();
	}
	TestCase_Maintenance(grIncidentFilter,'Test incident',RecordProcessorTest2,0,0,0,1);

	
	// Prepare a change request with an empty whitelist
	var grChangeRequest1 = new GlideRecord('change_request');
	if (grChangeRequest1.get(strChangeRequestSysId))
	{
		grChangeRequest1.assignment_group = strGroupAlphaSysId;
		grChangeRequest1.x_snc_datasilos_group_whitelist = '';
		grChangeRequest1.update();
	}
	TestCase_Maintenance(grChangeRequestFilter,'Test change_request',RecordProcessorTest2,0,0,0,1);

	
	// Prepare a problem with an empty whitelist
	var grProblem1 = new GlideRecord('problem');
	if (grProblem1.get(strProblemSysId))
	{
		grProblem1.assignment_group = strGroupAlphaSysId;
		grProblem1.x_snc_datasilos_group_whitelist = '';
		grProblem1.update();
	}
	TestCase_Maintenance(grProblemFilter,'Test problem',RecordProcessorTest2,0,0,0,1);
	
	
	
	
	
	// Test scenario 3 - ignoring ghost records
	
	var strIncidentSysId = testdata.CreateIncident('TEST_3','','CALLER','');
	
	var grIncidentFilter = new GlideRecord('incident');
	grIncidentFilter.addQuery('short_description','STARTSWITH',testdata.GetPrefix());


	
	// Prepare an incident with an empty whitelist and the ghost flag set to true
	var grIncident1 = new GlideRecord('incident');
	if (grIncident1.get(strIncidentSysId))
	{
		grIncident1.assignment_group = strGroupAlphaSysId;
		grIncident1.x_snc_datasilos_group_whitelist = '';
		grIncident1.x_snc_datasilos_ghost = true;
		grIncident1.update();
	}
	TestCase_Maintenance(grIncidentFilter,'Test incident',null,0,0,0,0);

	
	
	

	// TODO: Add a scenario to cover a group that is added to a silo AFTER the record has been initialized
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
