sys_script_include.script:
function GetStatus()
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetValue = x_snc_devtools.GetValue;
	var GetKeys = x_snc_devtools.GetKeys;
	var Debug = x_snc_devtools.Debug;

	var status = GetConfig();

	var aTables = GetKeys(GetValue(status,'tables'));
	for (var nTable = 0; nTable < aTables.length; nTable++)
	{
		var strTable = aTables[nTable];
		var table = GetValue(status,'tables',strTable); 

		var grTotal = new GlideAggregate(strTable);
		grTotal.query();
		var nTotal = grTotal.getRowCount();
		table['total'] = nTotal; 


		//var grHidden = new GlideRecord(strTable);
		var grHidden = new GlideAggregate(strTable);
		grHidden.addQuery(GetScopeFromRecord(grHidden)+'_global',false);
		grHidden.addQuery(GetScopeFromRecord(grHidden)+'_group_whitelist','');
		grHidden.addQuery(GetScopeFromRecord(grHidden)+'_supervisor_whitelist','');
		grHidden.query();
		var nHidden = grHidden.getRowCount();
		table['hidden'] = nHidden; 

		//var grUnmaintained = new GlideRecord(strTable);
		var grUnmaintained = new GlideAggregate(strTable);
		grUnmaintained.addQuery(GetScopeFromRecord(grUnmaintained)+'_last_maintenance',null);
		grUnmaintained.query();
		var nUnmaintained = grUnmaintained.getRowCount();
		table['unmaintained'] = nUnmaintained; 

		var strBackmarker = '';
		if (nUnmaintained == 0)
		{

			var grBackmarker = new GlideRecord(strTable);
			grBackmarker.orderBy(GetScopeFromRecord(grBackmarker)+'_last_maintenance');
			grBackmarker.setLimit(1);
			grBackmarker.query();
			if (grBackmarker.next())
			{
				strBackmarker = GetStringValue(grBackmarker.getValue(GetScopeFromRecord(grBackmarker)+'_last_maintenance'));
			}
		}
		table['backmarker'] = strBackmarker; 
	}

	return status;
}
