sys_script_include.script:
function RecordUpdateWhitelistsFromGroupsInTriggerFieldsByTypes(record,aTypes)
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var RenderValue = x_snc_devtools.RenderValue;

	var strTable = record.getRecordClassName();
	var aFields = GetTableWhitelistTriggerFields(strTable);
	//Debug('RecordUpdateWhitelistsFromGroupsInTriggerFieldsByTypes: '+RenderValue(aFields));

	for (var nIndexField = 0; nIndexField < aFields.length; nIndexField++)
	{
		var strField = aFields[nIndexField];
		//Debug('RecordUpdateWhitelistsFromGroupsInTriggerFieldsByTypes: '+'Field: '+strField);
		var ge = record.getElement(strField);

		var strGroupSysId = ge.toString();
		//Debug('RecordUpdateWhitelistsFromGroupsInTriggerFieldsByTypes: '+'Group read from '+strField+': '+strGroupSysId);
		RecordUpdateWhitelistsFromGroupByTypes(record,strGroupSysId,aTypes);
	}

}
