sys_script_include.script:
function AppBuilder(strAppSysIdOrScopeName,aTables,strAppMenuSeparator,nAppMenuOrderStart)
{
/*
	x_snc_datasilos.AppBuilder('x_snc_datasilos',[
		'incident',
		'incident_task',
		'change_request',
		'change_task',
		'problem',
		'problem_task',
		'sc_request',
		'sc_req_item',
		'sc_task']);

	x_snc_datasilos.AppBuilder('x_snc_datasilosci',[
		'cmdb_ci'],
		'CMDB',
		10000);
*/	
	
	
	
	
	
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetIntegerValue = x_snc_devtools.GetIntegerValue;
	var GetArrayValue = x_snc_devtools.GetArrayValue;
	var RenderValue = x_snc_devtools.RenderValue;
	var IsValidTable = x_snc_devtools.IsValidTable;
	var IsValidColumn = x_snc_devtools.IsValidColumn;
	var GlideRecordInsert = x_snc_devtools.GlideRecordInsert;
	var GlideRecordUpdate = x_snc_devtools.GlideRecordUpdate;
	var IsValidSysId = x_snc_devtools.IsValidSysId;
	var RecordGetValue = x_snc_devtools.RecordGetValue;
	var GetRecord = x_snc_devtools.GetRecord;

	strAppSysIdOrScopeName = GetStringValue(strAppSysIdOrScopeName);
	aTables = GetArrayValue(aTables);
	strAppMenuSeparator = GetStringValue(strAppMenuSeparator);
	nAppMenuOrderStart = GetIntegerValue(nAppMenuOrderStart);
	

	var strTable = '';
	
	function Log(strText)
	{
		var strTableLog = '';
		if (strTable != '')
		{
			strTableLog = strTable + ': ';
		}
		x_snc_datasilos.Log('AppBuilder: '+strTableLog+strText);
	}
	
	var strParentAppScope = 'x_snc_datasilos';
	if (strAppSysIdOrScopeName == 'x_snc_datasilos' || strAppSysIdOrScopeName == 'dafbffebdbb78450ba15a353059619eb')
	{
		strParentAppScope = '';
		strAppMenuSeparator = '';
	}
	else
	{
		if (strAppMenuSeparator == '')
		{
			Log('Error: No app menu separator was specified.');
			return false;
		}
		if (nAppMenuOrderStart < 10000)
		{
			Log('Error: app menu order start must be 10000 or higher.');
			return false;
		}
	}

	
	var grApp = x_snc_devtools.AppBuilder(
		strAppSysIdOrScopeName,
		strParentAppScope,
		strAppMenuSeparator,
		nAppMenuOrderStart); 
	if (grApp == false)
	{
		Log('Error: x_snc_devtools.AppBuilder returned false.');
		return false;
	}
	
	var strAppSysId = GetStringValue(grApp.sys_id);
	var strAppScope = GetStringValue(grApp.scope); 
	var strAppVersion = GetStringValue(grApp.version);
	var strDataSilosVersion = RecordGetValue('sys_app','dafbffebdbb78450ba15a353059619eb','version');
	
	var aTables = GetArrayValue(aTables);
	
	Log('AppBuilder is working on app '+strAppScope+'. The following tables will be supported: '+RenderValue(aTables));
	

	// We do not implement the extension point implemntation for DataSilos itself!
	if (strAppScope != 'x_snc_datasilos')
	{
		

		// Adding script include "DataSilosGetConfig"
		var grSSI = new GlideRecord('sys_script_include');
		grSSI.addQuery('sys_scope','=',strAppSysId);
		grSSI.addQuery('name','=','DataSilosGetConfig');
		grSSI.query();
		if (grSSI.getRowCount() != 0)
		{
			Log('Script include "DataSilosGetConfig" already exists...');
		}
		else
		{
			Log('Adding script include "DataSilosGetConfig"...');
			var grSSI = new GlideRecord('sys_script_include');
			grSSI.initialize();
			grSSI.name = 'DataSilosGetConfig';
			grSSI.description = "The DataSilosGetConfig is called by the DataSilos extension point instance class and returns the config object with all relevant changes and additions.";
			grSSI.sys_scope = strAppSysId;
			grSSI.access = 'public';
			grSSI.caller_access = '1';
			grSSI.script =
				"function DataSilosGetConfig(config)\n"
				+"{\n"
				+"\treturn config;\n"
				+"}";
			strSSI = GlideRecordInsert(grSSI);
			Log('Script include "DataSilosGetConfig": '+RenderValue(strSSI));	
		}


		// Adding script include "DataSilosGetRelatedRecords"
		var grSSI = new GlideRecord('sys_script_include');
		grSSI.addQuery('sys_scope','=',strAppSysId);
		grSSI.addQuery('name','=','DataSilosGetRelatedRecords');
		grSSI.query();
		if (grSSI.getRowCount() != 0)
		{
			Log('Script include "DataSilosGetRelatedRecords" already exists...');
		}
		else
		{
			Log('Adding script include "DataSilosGetRelatedRecords"...');
			var grSSI = new GlideRecord('sys_script_include');
			grSSI.initialize();
			grSSI.name = 'DataSilosGetRelatedRecords';
			grSSI.description = "The DataSilosGetRelatedRecords function is called by the DataSilos extension point instance class and returns an array of related records to the given record. If the record does not have any related records the function should return an empty array. If the function returns false, the default DataSilos function or other extensions will provide the related records.";
			grSSI.sys_scope = strAppSysId;
			grSSI.access = 'public';
			grSSI.caller_access = '1';
			grSSI.script = 
				"function DataSilosGetRelatedRecords(record)\n"
				+"{\n"
				+"\tvar IsValidRecord = x_snc_devtools.IsValidRecord;\n"
				+"\tvar RenderValue = x_snc_devtools.RenderValue;\n"
				+"\n"
				+"\tvar aRelatedRecords = [];\n"
				+"\n"
				+"\tvar strTable = x_snc_datasilos.GetSupportedTableFromRecord(record);\n"
				+"\n"
				+"\treturn false;\n"
				+"}";
			strSSI = GlideRecordInsert(grSSI);
			Log('Script include "DataSilosGetRelatedRecords": '+RenderValue(strSSI));	
		}	



		// Adding script include "DataSilosRecordInsert"
		var grSSI = new GlideRecord('sys_script_include');
		grSSI.addQuery('sys_scope','=',strAppSysId);
		grSSI.addQuery('name','=','DataSilosRecordInsert');
		grSSI.query();
		if (grSSI.getRowCount() != 0)
		{
			Log('Script include "DataSilosRecordInsert" already exists...');
		}
		else
		{
			Log('Adding script include "DataSilosRecordInsert"...');
			var grSSI = new GlideRecord('sys_script_include');
			grSSI.initialize();
			grSSI.name = 'DataSilosRecordInsert';
			grSSI.description = "The DataSilosRecordInsert function is called by the DataSilos extension point instance class and processes a record when it is inserted into a table. Return true if the record is handled by the function, false if not.";
			grSSI.sys_scope = strAppSysId;
			grSSI.access = 'public';
			grSSI.caller_access = '1';
			grSSI.script = 
				"function DataSilosRecordInsert(record)\n"
				+"{\n"
				+"\tvar IsValidRecord = x_snc_devtools.IsValidRecord;\n"
				+"\tvar RenderValue = x_snc_devtools.RenderValue;\n"
				+"\n"
				+"\tvar bResult = false;\n"
				+"\n"
				+"\tvar strTable = x_snc_datasilos.GetSupportedTableFromRecord(record);\n"
				+"\n"
				+"\treturn bResult;\n"
				+"}";
			strSSI = GlideRecordInsert(grSSI);
			Log('Script include "DataSilosRecordInsert": '+RenderValue(strSSI));	
		}	


		// Adding script include "DataSilosRecordUpdate"
		var grSSI = new GlideRecord('sys_script_include');
		grSSI.addQuery('sys_scope','=',strAppSysId);
		grSSI.addQuery('name','=','DataSilosRecordUpdate');
		grSSI.query();
		if (grSSI.getRowCount() != 0)
		{
			Log('Script include "DataSilosRecordUpdate" already exists...');
		}
		else
		{
			Log('Adding script include "DataSilosRecordUpdate"...');
			var grSSI = new GlideRecord('sys_script_include');
			grSSI.initialize();
			grSSI.name = 'DataSilosRecordUpdate';
			grSSI.description = "The DataSilosRecordUpdate function is called by the DataSilos extension point instance class and processes a record when it is updated. Return true if the record is handled by the function, false if not.";
			grSSI.sys_scope = strAppSysId;
			grSSI.access = 'public';
			grSSI.caller_access = '1';
			grSSI.script =
				"function DataSilosRecordUpdate(record)\n"
				+"{\n"
				+"\tvar IsValidRecord = x_snc_devtools.IsValidRecord;\n"
				+"\tvar RenderValue = x_snc_devtools.RenderValue;\n"
				+"\n"
				+"\tvar bResult = false;\n"
				+"\n"
				+"\tvar strTable = x_snc_datasilos.GetSupportedTableFromRecord(record);\n"
				+"\n"
				+"\treturn bResult;\n"
				+"}";
			strSSI = GlideRecordInsert(grSSI);
			Log('Script include "DataSilosRecordUpdate": '+RenderValue(strSSI));	
		}	
	}
	
	
	
	
	for (var nTable = 0; nTable < aTables.length; nTable++)
	{
		strTable = aTables[nTable];
		if (IsValidTable(strTable) == false)
		{
			Log('IsValidTable("'+strTable+'") returned false. Ignoring table.');
		}
		else
		{
			Log('Working on table "'+strTable+'"...');

			var strTableScope = '';
			var grSDO = new GlideRecord('sys_db_object');
			grSDO.addQuery('name','=',strTable);
			grSDO.query();
			if (grSDO.next())
			{
				var grScope = GetRecord('sys_scope',grSDO.sys_scope);
				if (grScope != null)
				{
					strTableScope = GetStringValue(grScope.scope);
				}
			}
			
			function MakeColumnName(strColumn)
			{
				
				if (strAppScope == strTableScope)
				{
					return strColumn;
				}
				return strAppScope + '_' + strColumn;
			}
			
			var strColumn = MakeColumnName('global');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Global" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Global Visibility';
				grSD.default_value = 'false';
				grSD.element = strColumn;
				grSD.internal_type = 'boolean';
				grSD.name = strTable;
				grSD.reference = null;
				grSD.sys_scope = strAppSysId;
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Global Visibility" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'global',strAppScope);
			}
			
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'global', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'global', strFieldSysId, 'write');
			}
			
			
			
			strColumn = MakeColumnName('supervisor_whitelist');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Supervisor Whitelist" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.attributes = 'edge_encryption_enabled=true,no_sort=true,slushbucket_ref_no_expand=true';
				grSD.audit = false;
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Supervisor Whitelist';
				grSD.element = strColumn;
				grSD.internal_type = 'glide_list';
				grSD.max_length = 4000;
				grSD.name = strTable;
				grSD.reference = 'x_snc_datasilos_datasilos';
				grSD.reference_cascade_rule = 'none';
				grSD.sys_scope = strAppSysId;
				grSD.use_reference_qualifier = 'simple';
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Supervisor Whitelist" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'supervisor_whitelist',strAppScope);
			}
			
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'supervisor_whitelist', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'supervisor_whitelist', strFieldSysId, 'write');
			}
			
			
			strColumn = MakeColumnName('fulfiller_whitelist');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Fulfiller Whitelist" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.attributes = 'edge_encryption_enabled=true,no_sort=true,slushbucket_ref_no_expand=true';
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Fulfiller Whitelist';
				grSD.element = strColumn;
				grSD.internal_type = 'glide_list';
				grSD.max_length = 4000;
				grSD.name = strTable;
				grSD.reference = 'x_snc_datasilos_datasilos';
				grSD.reference_cascade_rule = 'none';
				grSD.sys_scope = strAppSysId;
				grSD.use_reference_qualifier = 'simple';
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Fulfiller Whitelist" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'fulfiller_whitelist',strAppScope);
			}
			
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'fulfiller_whitelist', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'fulfiller_whitelist', strFieldSysId, 'write');
			}


			strColumn = MakeColumnName('group_whitelist');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Group Whitelist" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.attributes = 'edge_encryption_enabled=true,no_sort=true,slushbucket_ref_no_expand=true';
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Group Whitelist';
				grSD.element = strColumn;
				grSD.internal_type = 'glide_list';
				grSD.max_length = 4000;
				grSD.name = strTable;
				grSD.reference = 'sys_user_group';
				grSD.reference_cascade_rule = 'none';
				grSD.sys_scope = strAppSysId;
				grSD.use_reference_qualifier = 'simple';
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Group Whitelist" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'group_whitelist',strAppScope);
			}
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'group_whitelist', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'group_whitelist', strFieldSysId, 'write');
			}
			
			
			strColumn = MakeColumnName('security_clearance');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Security Clearance" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Security Clearance';
				grSD.default_value = '0';
				grSD.element = strColumn;
				grSD.internal_type = 'integer';
				grSD.name = strTable;
				grSD.sys_scope = strAppSysId;
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Security Clearance" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'security_clearance',strAppScope);
			}
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'security_clearance', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'security_clearance', strFieldSysId, 'write');
			}	
			
			strColumn = MakeColumnName('ghost');
			
			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Ghost" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Ghost';
				grSD.default_value = 'false';
				grSD.element = strColumn;
				grSD.internal_type = 'boolean';
				grSD.name = strTable;
				grSD.sys_scope = strAppSysId;
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Ghost" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'ghost',strAppScope);
			}
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'ghost', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'ghost', strFieldSysId, 'write');
			}
			
			strColumn = MakeColumnName('last_maintenance');

			var strFieldSysId = '';
			var grSD = new GlideRecord('sys_dictionary');
			grSD.addQuery('name','=',strTable);
			grSD.addQuery('element','=',strColumn);
			grSD.query();
			if (grSD.getRowCount() != 0)
			{
				grSD.next();
				strFieldSysId = GetStringValue(grSD.sys_id);
				Log('Field "Last Maintenance" already exists in table "'+strTable+'".');
			}
			else
			{
				var grSD = new GlideRecord('sys_dictionary');
				grSD.initialize();
				grSD.active = true;
				grSD.calculation = "<![CDATA[ (function calculatedFieldValue(current) { // Add your code here return ''; // return the calculated value })(current); ]]>";
				grSD.column_label = 'DataSilos Last Maintenance';
				grSD.default_value = '';
				grSD.element = strColumn;
				grSD.internal_type = 'glide_date_time';
				grSD.name = strTable;
				grSD.sys_scope = strAppSysId;
				strFieldSysId = GlideRecordInsert(grSD);
				Log('Adding the "Last Maintenance" field: '+RenderValue(strFieldSysId));
				AppBuilderFieldLabelsSetScope(strAppScope,strTable,'last_maintenance',strAppScope);

			}
			if (IsValidSysId(strFieldSysId) == true)
			{
				AppBuilderAddFieldACL(strAppScope, strTable, 'last_maintenance', strFieldSysId, 'read');
				AppBuilderAddFieldACL(strAppScope, strTable, 'last_maintenance', strFieldSysId, 'write');
			}
			
			
			
			
			Log('Working on table maintenance scheduled job...');
			var strJobName = 'DataSilos - Maintenance - '+strTable;
			var grSA = new GlideRecord('sysauto_script');
			grSA.addQuery('name','=',strJobName);
			grSA.addQuery('sys_scope','=',strAppSysId);
			grSA.query();
			if (grSA.getRowCount() != 0)
			{
				grSA.next();
				Log('Maintenance scheduled job already exists for table "'+strTable+'".');
			}
			else
			{
				var grSA = new GlideRecord('sysauto_script');
				grSA.initialize();
				grSA.name = strJobName;
				grSA.sys_scope = strAppSysId;
				grSA.active = true;
				grSA.conditional = false;
				grSA.run_as = '6816f79cc0a8016401c5a33be04be441'; // system admin
				grSA.run_dayofmonth = '1';
				grSA.run_dayofweek = '1';
				grSA.run_time = '1969-12-31 23:00:00';
				grSA.run_period.setDateNumericValue(600000); // 10 minutes
				grSA.run_type = 'periodically';
				var strScript = 
					"var grRecords = new GlideRecord('"+strTable+"');\n"
					+"x_snc_datasilos.Maintenance(grRecords);";
				grSA.script = strScript;				
				var strJobSysId = GlideRecordInsert(grSA);
				Log('Scheduled job: '+RenderValue(strJobSysId));
			}
					
			
			Log('Working on business rule...');
			var strBusinessRuleName = GetBusinessRuleName(strTable);
			var grBR = new GlideRecord('sys_script');
			grBR.addQuery('name','=',strBusinessRuleName);
			grBR.addQuery('sys_scope','=',strAppSysId);
			grBR.query();
			if (grBR.getRowCount() != 0)
			{
				grBR.next();
				Log('Business rule already exists for table "'+strTable+'".');
			}
			else
			{
				var grBR = new GlideRecord('sys_script');
				grBR.initialize();
				grBR.active = true;
				grBR.name = strBusinessRuleName;
				grBR.sys_scope = strAppSysId;
				grBR.action_insert = true;
				grBR.action_update = true;
				grBR.action_query = true;
				grBR.advanced = true;
				grBR.when = 'before';
				grBR.order = 10000;
				grBR.collection = strTable;
				var strScript = 
					"(function executeRule(current, previous /*null when async*/) {\n"
					+"\tx_snc_datasilos.ExecuteBusinessRule(current, previous);\n"
					+"})(current, previous);";
				grBR.script = strScript;				
				var strBusinessRuleSysId = GlideRecordInsert(grBR);
				Log('Business rule: '+RenderValue(strBusinessRuleSysId));
			}
			
			if (strAppScope != 'x_snc_datasilos')
			{
			
				var grSSI = new GlideRecord('sys_script_include');
				grSSI.addQuery('sys_scope','=',strAppSysId);
				grSSI.addQuery('name','=','DataSilosGetConfig');
