				{
					Log('Modifying script include "DataSilosGetConfig"...');
					var strScript = GetStringValue(grSSI.script);
					if (strScript.indexOf('return config;') == -1)
					{
						Log('ERROR: Script include "DataSilosGetConfig" DOES NOT SEEM TO BE IMPLEMENTED CORRECTLY...');
					}
					if (strScript.indexOf("config['tables']['"+strTable+"'] =") == -1)
					{
						strScriptTableConfig = 
							"config['tables']['"+strTable+"'] =\n"
							+"\t{\n"
							+"\t\t'scope': '"+strAppScope+"',\n"
							+"\t\t'active': true,\n"
							+"\t\t'avoid_expensive_queries': false,\n"
