							+"\treturn config;";
						strScript = strScript.replace('return config;',strScriptTableConfig);
						grSSI.script = strScript;
						strSSI = GlideRecordUpdate(grSSI);
						Log('Updating script include "DataSilosGetConfig": '+RenderValue(strSSI));	
					}

				}


				var grSSI = new GlideRecord('sys_script_include');
				grSSI.addQuery('sys_scope','=',strAppSysId);
				grSSI.addQuery('name','=','DataSilosGetRelatedRecords');
				grSSI.query();
				if (grSSI.next() == false)
				{
					Log('ERROR: Script include "DataSilosGetRelatedRecords" DOES NOT EXIST...');
				}
				else
				{
					Log('Modifying script include "DataSilosGetRelatedRecords"...');
					var strScript = GetStringValue(grSSI.script);
					if (strScript.indexOf('return false;') == -1)
					{
						Log('ERROR: Script include "DataSilosGetRelatedRecords" DOES NOT SEEM TO BE IMPLEMENTED CORRECTLY...');
					}
					if (strScript.indexOf("if (strTable == '"+strTable+"')") == -1)
					{
						strScriptTableConfig =
							"if (strTable == '"+strTable+"')\n"
							+"\t{\n"
							+"\t\t// Add your code here...\n"
							+"\t\treturn aRelatedRecords;\n"
							+"\t}\n"
							+"\t\n"
							+"\treturn false;";
						strScript = strScript.replace('return false;',strScriptTableConfig);
						grSSI.script = strScript;
						strSSI = GlideRecordUpdate(grSSI);
						Log('Updating script include "DataSilosGetRelatedRecords": '+RenderValue(strSSI));	
					}

				}



				var grSSI = new GlideRecord('sys_script_include');
				grSSI.addQuery('sys_scope','=',strAppSysId);
				grSSI.addQuery('name','=','DataSilosRecordInsert');
				grSSI.query();
				if (grSSI.next() == false)
				{
					Log('ERROR: Script include "DataSilosRecordInsert" DOES NOT EXIST...');
				}
				else
				{
					Log('Modifying script include "DataSilosRecordInsert"...');
					var strScript = GetStringValue(grSSI.script);
					if (strScript.indexOf('var strTable = x_snc_datasilos.GetSupportedTableFromRecord(record);') == -1)
					{
						Log('ERROR: Script include "DataSilosRecordInsert" DOES NOT SEEM TO BE IMPLEMENTED CORRECTLY...');
					}
					if (strScript.indexOf("if (strTable == '"+strTable+"')") == -1)
					{
						strScriptTableConfig = 
							"if (strTable == '"+strTable+"')\n"
							+"\t{\n"
							+"\t\t//Add your code here...\n"
							+"\t\tbResult = true;\n"
							+"\t}\n"
							+"\t\n"
							+"\treturn bResult;";
						strScript = strScript.replace('return bResult;',strScriptTableConfig);
						grSSI.script = strScript;
						strSSI = GlideRecordUpdate(grSSI);
						Log('Updating script include "DataSilosRecordInsert": '+RenderValue(strSSI));	
					}
				}



				var grSSI = new GlideRecord('sys_script_include');
				grSSI.addQuery('sys_scope','=',strAppSysId);
				grSSI.addQuery('name','=','DataSilosRecordUpdate');
				grSSI.query();
				if (grSSI.next() == false)
				{
					Log('ERROR: Script include "DataSilosRecordUpdate" DOES NOT EXIST...');
				}
				else
				{
					Log('Modifying script include "DataSilosRecordUpdate"...');
					var strScript = GetStringValue(grSSI.script);
					if (strScript.indexOf('var strTable = x_snc_datasilos.GetSupportedTableFromRecord(record);') == -1)
					{
						Log('ERROR: Script include "DataSilosRecordUpdate" DOES NOT SEEM TO BE IMPLEMENTED CORRECTLY...');
					}
					if (strScript.indexOf("if (strTable == '"+strTable+"')") == -1)
					{
						strScriptTableConfig = 
							"if (strTable == '"+strTable+"')\n"
							+"\t{\n"
							+"\t\t//Add your code here...\n"
							+"\t\tbResult = true;\n"
							+"\t}\n"
							+"\t\n"
							+"\treturn bResult;";
						strScript = strScript.replace('return bResult;',strScriptTableConfig);
						grSSI.script = strScript;
						strSSI = GlideRecordUpdate(grSSI);
						Log('Updating script include "DataSilosRecordUpdate": '+RenderValue(strSSI));	
					}
				}
			}
			
			
			
			Log('Working on UI list...');
			var grUL = new GlideRecord('sys_ui_list');
			grUL.addQuery('name','=',strTable);
			grUL.addQuery('sys_scope','=',strAppSysId);
			grUL.query();
			if (grUL.getRowCount() != 0)
			{
				grUL.next();
				Log('UI list (layout) already exists for table "'+strTable+'".');
			}
			else
			{
				var grUL = new GlideRecord('sys_ui_list');
				grUL.initialize();
				//grUL.active = true;
				grUL.name = strTable;
				grUL.sys_scope = strAppSysId;
				grUL.view = '268b1d0adb849090ba15a35305961902'; // the DataSilos view
				var strUiListSysId = GlideRecordInsert(grUL);
				Log('UI list (layout) : '+RenderValue(strUiListSysId));
				if (IsValidSysId(strUiListSysId) == true)
				{
					
					var nPosition = 0;
					function AddUiListElement(strColumn)
					{
						var strElement = strColumn;
						if (IsValidColumn(strTable,strElement) == false)
						{
							strElement = strAppScope+'_' + strColumn;
							if (IsValidColumn(strTable,strElement) == false)
							{
								return false;
							}
						}
						
						var grULE = new GlideRecord('sys_ui_list_element');
						grULE.initialize();
						grULE.element = strElement;
						grULE.list_id = strUiListSysId;
						grULE.position = nPosition++;
						grULE.sys_scope = strAppSysId;
						//grULE.view = '268b1d0adb849090ba15a35305961902'; // the DataSilos view
						var strUiListElementSysId = GlideRecordInsert(grULE);
						Log('Added UI list element "'+strColumn+'": '+RenderValue(strUiListElementSysId));
						
						
					}
					
					AddUiListElement('number');
					AddUiListElement('name');
					AddUiListElement('short_description');
					AddUiListElement('sys_class_name');
					AddUiListElement('sys_created_on');
					AddUiListElement('global');
					AddUiListElement('supervisor_whitelist');
					AddUiListElement('fulfiller_whitelist');
					AddUiListElement('group_whitelist');
					AddUiListElement('security_clearance');
					AddUiListElement('ghost');
					AddUiListElement('last_maintenance');
					AddUiListElement('opened_by');
					AddUiListElement('caller_id');
					AddUiListElement('requested_for');
					AddUiListElement('requested_by');
					AddUiListElement('assignment_group');
	
					
					
					
				}
			}
		}
	}
	
	strTable = '';
	
	
	if (strAppScope != 'x_snc_datasilos')
	{
	
		// Modify AppGetDependencies script include
		var grSI = new GlideRecord('sys_script_include');
		grSI.addQuery('sys_scope','=',strAppSysId);
		grSI.addQuery('name','=','AppGetDependencies');
		grSI.query();
		while (grSI.next())
		{
			var strScript = GetStringValue(grSI.script);
			if (strScript.indexOf("name: 'DataSilos'") == -1)
			{
				Log('Modifying script include AppGetDependencies to add dependency to DataSilos...');

				strScriptDataSilosDependency =
					"\t,\n"
					+"\t\t{\n"
					+"\t\t\tname: 'DataSilos',\n"
					+"\t\t\tversion: '"+strDataSilosVersion+"',\n"
					+"\t\t\turl: 'https://github.com/saschawildgrube/servicenow-datasilos/',\n"
					+"\t\t\twebsite_url: 'https://www.wildgrube.com/servicenow-datasilos'\n"
					+"\t\t}\n"
					+"\t];";

				strScript = strScript.replace('];',strScriptDataSilosDependency);
				grSI.script = strScript;
				GlideRecordUpdate(grSI);
			}
		}





		// Adding script include "DataSilos" which implements the extension point
		var strDataSilosScriptInclude = false;
		var strScript = RecordGetValue('sys_extension_point','68e24c12db76b8104647dad1f396198e','example');
		strScript = strScript.replaceAll('x_this_app_scope',strAppScope);
		var grSSI = new GlideRecord('sys_script_include');
		grSSI.addQuery('sys_scope','=',strAppSysId);
		grSSI.addQuery('name','=','DataSilos');
		grSSI.query();
		if (grSSI.getRowCount() != 0)
		{
			Log('Script include "DataSilos" already exists. Updating it...');
			grSSI.next();
			grSSI.script = strScript;
			GlideRecordUpdate(grSSI);
			strDataSilosScriptInclude = grSSI.sys_id;
		}
		else
		{
			Log('Adding script include "DataSilos"...');
			var grSSI = new GlideRecord('sys_script_include');
			grSSI.initialize();
			grSSI.name = 'DataSilos';
			grSSI.description = "Implements the extension point for DataSilos.";
			grSSI.sys_scope = strAppSysId;
			grSSI.access = 'public';
			grSSI.caller_access = '1';
			grSSI.script = strScript;
			strDataSilosScriptInclude = GlideRecordInsert(grSSI);
		}
		Log('Script include "DataSilos": '+RenderValue(strDataSilosScriptInclude));

		// Adding extension point instance
		var strEPI = false;
		var grEPI = new GlideRecord('sys_extension_instance');
		grEPI.addQuery('sys_scope','=',strAppSysId);
		grEPI.addQuery('point','=','x_snc_datasilos.DataSilos');
		grEPI.query();
		if (grEPI.getRowCount() != 0)
		{
			grEPI.next();
			Log('Extension point instance for "DataSilos" already exists: '+grEPI.sys_id);
		}
		else
		{
			Log('Adding extension point instance for "DataSilos"...');
			var grEPI = new GlideRecord('sys_extension_instance');
			grEPI.initialize();
			grEPI.active = true;
			grEPI.point = 'x_snc_datasilos.DataSilos';
			grEPI.sys_scope = strAppSysId;
			grEPI.script_include = strDataSilosScriptInclude;
			grEPI.order = 100;
			strEPI = GlideRecordInsert(grEPI);
			Log('Adding extension point instance for "DataSilos": '+RenderValue(strEPI));
		}
	}
	
	
	Log('Done!');
	
	return grApp;


	function AppBuilderFieldLabelsSetScope(strAppScope,strTable,strColumn,strScope)
	{
		var grSD = new GlideRecord('sys_documentation');
		grSD.addQuery('name','=',strTable);
		grSD.addQuery('element','=',strAppScope+'_'+strColumn);
		grSD.query();
		while (grSD.next())
		{
			grSD.sys_scope = strScope;
			GlideRecordUpdate(grSD);
		}
	}

	function AppBuilderAddFieldACL(strAppScope, strTable, strFieldName, strFieldSysId,strOperation)
	{
		strAppScope = GetStringValue(strAppScope);
		strTable = GetStringValue(strTable);
		strFieldName = GetStringValue(strFieldName);
		strFieldSysId = GetStringValue(strFieldSysId);
		strOperation = GetStringValue(strOperation);

		Log('Working on ACL for "'+strOperation+'" operation...');
		var strAclName = strTable+'.'+MakeColumnName(strFieldName);
		var strAclSysId = '';
		var grSSA = new GlideRecord('sys_security_acl');
		grSSA.addQuery('type','=','record');
		grSSA.addQuery('operation','=',strOperation);
		grSSA.addQuery('name','=',strAclName);
		grSSA.query();
		if (grSSA.getRowCount() != 0)
		{
			grSSA.next();
			strAclSysId = GetStringValue(grSSA.sys_id);
			Log('ACL for "'+strOperation+'" operation already exists for field "'+strFieldName+'" in table "'+strTable+'".');
		}
		else
		{
			var grSSA = new GlideRecord('sys_security_acl');
			grSSA.initialize();
			grSSA.active = true;
			grSSA.admin_overrides = true;
			grSSA.advanced = false;
			grSSA.name  = strAclName; 
			grSSA.operation = strOperation;
			grSSA.sys_scope = strAppSysId;
			grSSA.type = 'record';
			strAclSysId = GlideRecordInsert(grSSA);
			Log('Adding ACL for "'+strOperation+'" operation for field "'+strFieldName+'": '+RenderValue(strAclSysId));
		}


		if (IsValidSysId(strAclSysId) == true)
		{
			// Add Role
			Log('Working on ACL role...');
			var strAclRoleSysId = '';
			var grSSAR = new GlideRecord('sys_security_acl_role');
			grSSAR.addQuery('sys_security_acl','=',strAclSysId);
			grSSAR.addQuery('sys_user_role','=','23df0da71b48d490a89720a8ec4bcb72'); // x_snc_datasilos.manager
			grSSAR.query();
			if (grSSAR.getRowCount() != 0)
			{
				grSSAR.next();
				strAclRoleSysId = GetStringValue(grSSAR.sys_id);
				Log('Role for ACL for "'+strOperation+'" operation already exists for field "'+strFieldName+'" in table "'+strTable+'".');
			}
			else
			{
				var grSSAR = new GlideRecord('sys_security_acl_role');
				grSSAR.initialize();
				grSSAR.sys_scope = strAppSysId;
				grSSAR.sys_security_acl = strAclSysId;
				grSSAR.sys_user_role = '23df0da71b48d490a89720a8ec4bcb72'; // x_snc_datasilos.manager;
				strAclRoleSysId = GlideRecordInsert(grSSAR);
				Log('Role for "'+strFieldName+'" ACL for "'+strOperation+'" operation: '+RenderValue(strAclRoleSysId));
			}
		}
	}


